mapper.select:
SELECT id,
       postTitle,
       postContents,
       postItemPrice,
       postItemName,
       postCategory,
       createdAt,
       updatedAt,
       memberIdFk
FROM post
WHERE id = #{id}
   OR postTitle = #{postTitle}
   OR postItemName = #{postItemName}
   OR postCategory = #{postCategory}
   OR memberIdFk = #{memberIdFk}

mapper.update:
UPDATE post
SET postTitle     = #{newPost.postTitle},
    postContents  = #{newPost.postContents},
    postItemPrice = #{newPost.postItemPrice},
    postItemName  = #{newPost.postItemName},
    postCategory  = #{newPost.postCategory}
WHERE id = #{id}
  AND memberIdFk = #{sessionMemberId}

mapper.delete:
DELETE
FROM post
WHERE id = #{id}
  AND memberIdFk = #{sessionMemberId}
